import { StyleSheet } from "react-native";

export const styles = StyleSheet.create({
  mainContainer: {
    flex: 1, // Garante que a View principal ocupe a tela inteira
    justifyContent: "space-between",
  },
  Container: {
    flex: 1,
    justifyContent: "center",
    alignItems: "center",
    paddingHorizontal: 20,
  },
  text1: {
    color: "#616161",
    fontSize: 18,
    fontWeight: "bold",
    marginVertical: 10,
  },
  divImgImpt: {
    flexDirection: "row",
    alignItems: "center",
    marginVertical: 10,
    width: "100%", // Para garantir que ocupe a largura do container
  },
  imageCadeado: {
    width: 24,
    height: 24,
    marginRight: 10,
  },
  input: {
    flex: 1,
    borderBottomWidth: 1,
    borderBottomColor: "#BDBDBD",
    paddingVertical: 5,
    color: "#424242",
  },
  text2: {
    color: "#BDBDBD",
    textAlign: "center",
    marginVertical: 10,
  },
  divButton: {
    flexDirection: "row",
    justifyContent: "space-around",
    width: "100%",
    marginVertical: 5,
  },
  button: {
    flex: 1,
    alignItems: "center",
    justifyContent: "center",
    paddingVertical: 15,
    marginHorizontal: 5,
    backgroundColor: "#EF6C00",
    borderRadius: 8,
  },
  buttonText: {
    color: "#FAFAFA",
    fontWeight: "bold",
  },
  imageApagar: {
    width: 20,
    height: 20,
  },
  buttonAcesso: {
    marginTop: 20,
    backgroundColor: "#1565C0",
    paddingVertical: 15,
    paddingHorizontal: 40,
    borderRadius: 8,
    alignItems: "center",
  },
  buttonTextAcesso: {
    color: "#FAFAFA",
    fontWeight: "bold",
    fontSize: 16,
  },
});
