import { StyleSheet, Dimensions } from "react-native";

// Pegando a largura da tela para usar valores dinâmicos
const screenWidth = Dimensions.get("window").width;

export const styles = StyleSheet.create({
  header: {
    flexDirection: "row",
    alignItems: "center",
    backgroundColor: "#EF6C00",
    paddingTop: 30,
    paddingBottom: 10,
    paddingHorizontal: 20,
    width: "100%",
    height: 80, // Definindo uma altura confortável para o cabeçalho
  },
  imagePerfilLogado: {
    borderRadius: 25,
    width: 40,
    height: 40,
  },
  viewHeader: {
    flex: 1, // Faz com que o nome ocupe o espaço restante
    paddingLeft: 10,
    flexDirection: "row", // Organiza os elementos horizontalmente
    alignItems: "center", // Alinha o nome e a seta verticalmente
  },
  text1Header: {
    color: "#FAFAFA",
    fontWeight: "bold",
    fontSize: 16,
    paddingBottom: 4,
  },
  iconSetinhaWrapper: {
    flexDirection: "row", // Garante que a seta fique ao lado do nome
    alignItems: "center",
    marginLeft: 10, // Ajusta o espaçamento entre o nome e a seta
  },
  iconsContainer: {
    flexDirection: "row",
    alignItems: "center",
    marginLeft: "auto", // Empurra os ícones para a direita
    justifyContent: "space-between",
    width: 120, // Largura suficiente para os 3 ícones
  },
  imageSetinhaLogado: {
    width: 12,
    height: 10,
  },
  imageLupa: {
    width: 30,
    height: 30,
  },
  imageSininho: {
    width: 30,
    height: 30,
  },
});
