import { StyleSheet, Dimensions } from "react-native";

// Pegando a largura da tela para usar valores dinâmicos
const screenWidth = Dimensions.get("window").width;

export const styles = StyleSheet.create({
  header: {
    flexDirection: "row",
    alignItems: "center",
    backgroundColor: "#EF6C00",
    paddingTop: 30,
    paddingBottom: 10,
    paddingHorizontal: 20,
    width: "100%",
    height: 80, // Definindo uma altura confortável para o cabeçalho
  },
  imagePerfilLogado: {
    borderRadius: 25,
    width: 40,
    height: 40,
  },
  viewHeader: {
    flexDirection: "row", // Alinha nome e seta na horizontal
    alignItems: "center", // Alinha a seta com o nome
    paddingLeft: 10,
    flex: 1, // Ocupa o espaço restante
  },
  text1Header: {
    color: "#FAFAFA",
    fontWeight: "bold",
    fontSize: 16,
    paddingBottom: 4,
  },
  iconsContainer: {
    flexDirection: "row",
    alignItems: "center",
    marginLeft: "auto",
    justifyContent: "space-between",
    width: 120,
  },
  imageSetinhaLogado: {
    width: 12,
    height: 10,
    marginLeft: 10,
  },
  imageLupa: {
    width: 30,
    height: 30,
  },
  imageSininho: {
    width: 30,
    height: 30,
  },
});
