import { StyleSheet, Dimensions } from "react-native";

// Pegando a largura da tela para usar valores dinâmicos
const screenWidth = Dimensions.get("window").width;

export const styles = StyleSheet.create({
  header: {
    flexDirection: "row",
    alignItems: "center",
    backgroundColor: "#FAFAFA",
    paddingTop: 30,
    paddingBottom: 10,
    paddingHorizontal: 20,
    width: "100%",
    height: 80, // Definindo uma altura confortável para o cabeçalho
    borderBottomWidth: 1,
    borderBottomColor: "#E0E0E0", // Borda sutil para separar o cabeçalho
  },
  imagePerfilLogado: {
    borderRadius: 25,
    width: 40,
    height: 40,
  },
  viewHeader: {
    flex: 1, // Ajuste para ocupar o espaço restante
    paddingLeft: 20,
  },
  text1Header: {
    color: "#EF6C00",
    fontWeight: "bold",
    fontSize: 16,
    paddingBottom: 4,
  },
  iconsContainer: {
    flexDirection: "row",
    alignItems: "center",
    marginLeft: "auto", // Utilizando auto para empurrar os ícones para a direita
    justifyContent: "space-between",
  },
  imageSetaLogado: {
    width: 20,
    height: 10,
    marginRight: 20,
  },
  imageLupa: {
    width: 24,
    height: 24,
    marginRight: 20,
  },
  imageSino: {
    width: 24,
    height: 24,
  },
});
